package com;

public class Pattern7 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
/*

A B C D E
B C D E
C D E
D E
E
 * 
 */
		for(char i='A'; i<='E';i++)
		{
			for(char j=i; j<='E';j++)                       
				
			{                                            
				System.out.print(j+ " ");
			}
			System.out.println();
		}


	}



		
		
	}


